using System;
using Xunit;
using Xunit.Sdk;

namespace UnitsNet.Tests.CustomCode
{
    public class MassDistanceTests : MassDistanceTestsBase
    {
        // Override properties in base class here
        protected override bool SupportsSIUnitSystem => false;
        protected override double KilogramKilometersInOneKilogramKilometer => 1;
        protected override double ShortTonMilesInOneKilogramKilometer => 0.0006849315;
        protected override double TonneKilometersInOneKilogramKilometer => 0.001;

        [Fact]
        public void DistanceMultipliedByMassEqualsMassDistance()
        {
            Length miles = Length.FromMiles(2015);
            Mass tons = Mass.FromShortTons(14);
            MassDistance massDistance = miles * tons;
            Assert.Equal(28209, Math.Round(massDistance.ShortTonMiles));
            Assert.Equal(41186, Math.Round(massDistance.TonneKilometers));
        }
    }
}
