            Assert.AreEqual(0, voltac.CompareTo(voltac));
            Assert.Greater(voltac.CompareTo(ElectricPotentialAc.Zero), 0);
            Assert.Less(ElectricPotentialAc.Zero.CompareTo(voltac), 0);
        }

