using System;
using UnitsNet.Units;
using Xunit;

// Disable build warning CS1718: Comparison made to same variable; did you mean to compare something else?
#pragma warning disable 1718

// ReSharper disable once CheckNamespace
namespace UnitsNet.Tests
{
    /// <summary>
    /// Test of Irradiation.
    /// </summary>
// ReSharper disable once PartialTypeWithSinglePart
    public abstract partial class IrradiationTestsBase
    {
        protected abstract double JoulesPerMeterSquaredInOneJoulePerMeterSquared { get; }
        protected abstract double KilowattHoursPerMeterSquaredInOneJoulePerMeterSquared { get; }
        protected abstract double WattHoursPerMeterSquaredInOneJoulePerMeterSquared { get; }

// ReSharper disable VirtualMemberNeverOverriden.Global
        protected virtual double JoulesPerMeterSquaredTolerance { get { return 1e-5; } }
        protected virtual double KilowattHoursPerMeterSquaredTolerance { get { return 1e-5; } }
        protected virtual double WattHoursPerMeterSquaredTolerance { get { return 1e-5; } }
// ReSharper restore VirtualMemberNeverOverriden.Global

        [Fact]
        public void JoulePerMeterSquaredToIrradiationUnits()
        {
            Irradiation joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            AssertEx.EqualTolerance(JoulesPerMeterSquaredInOneJoulePerMeterSquared, joulepermetersquared.JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(KilowattHoursPerMeterSquaredInOneJoulePerMeterSquared, joulepermetersquared.KilowattHoursPerMeterSquared, KilowattHoursPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(WattHoursPerMeterSquaredInOneJoulePerMeterSquared, joulepermetersquared.WattHoursPerMeterSquared, WattHoursPerMeterSquaredTolerance);
        }

        [Fact]
        public void FromValueAndUnit()
        {
            AssertEx.EqualTolerance(1, Irradiation.From(1, IrradiationUnit.JoulePerMeterSquared).JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(1, Irradiation.From(1, IrradiationUnit.KilowattHourPerMeterSquared).KilowattHoursPerMeterSquared, KilowattHoursPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(1, Irradiation.From(1, IrradiationUnit.WattHourPerMeterSquared).WattHoursPerMeterSquared, WattHoursPerMeterSquaredTolerance);
        }

        [Fact]
        public void As()
        {
            var joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            AssertEx.EqualTolerance(JoulesPerMeterSquaredInOneJoulePerMeterSquared, joulepermetersquared.As(IrradiationUnit.JoulePerMeterSquared), JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(KilowattHoursPerMeterSquaredInOneJoulePerMeterSquared, joulepermetersquared.As(IrradiationUnit.KilowattHourPerMeterSquared), KilowattHoursPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(WattHoursPerMeterSquaredInOneJoulePerMeterSquared, joulepermetersquared.As(IrradiationUnit.WattHourPerMeterSquared), WattHoursPerMeterSquaredTolerance);
        }

        [Fact]
        public void ConversionRoundTrip()
        {
            Irradiation joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            AssertEx.EqualTolerance(1, Irradiation.FromJoulesPerMeterSquared(joulepermetersquared.JoulesPerMeterSquared).JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(1, Irradiation.FromKilowattHoursPerMeterSquared(joulepermetersquared.KilowattHoursPerMeterSquared).JoulesPerMeterSquared, KilowattHoursPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(1, Irradiation.FromWattHoursPerMeterSquared(joulepermetersquared.WattHoursPerMeterSquared).JoulesPerMeterSquared, WattHoursPerMeterSquaredTolerance);
        }

        [Fact]
        public void ArithmeticOperators()
        {
            Irradiation v = Irradiation.FromJoulesPerMeterSquared(1);
            AssertEx.EqualTolerance(-1, -v.JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(2, (Irradiation.FromJoulesPerMeterSquared(3)-v).JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(2, (v + v).JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(10, (v*10).JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(10, (10*v).JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(2, (Irradiation.FromJoulesPerMeterSquared(10)/5).JoulesPerMeterSquared, JoulesPerMeterSquaredTolerance);
            AssertEx.EqualTolerance(2, Irradiation.FromJoulesPerMeterSquared(10)/Irradiation.FromJoulesPerMeterSquared(5), JoulesPerMeterSquaredTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            Irradiation oneJoulePerMeterSquared = Irradiation.FromJoulesPerMeterSquared(1);
            Irradiation twoJoulesPerMeterSquared = Irradiation.FromJoulesPerMeterSquared(2);

            Assert.True(oneJoulePerMeterSquared < twoJoulesPerMeterSquared);
            Assert.True(oneJoulePerMeterSquared <= twoJoulesPerMeterSquared);
            Assert.True(twoJoulesPerMeterSquared > oneJoulePerMeterSquared);
            Assert.True(twoJoulesPerMeterSquared >= oneJoulePerMeterSquared);

            Assert.False(oneJoulePerMeterSquared > twoJoulesPerMeterSquared);
            Assert.False(oneJoulePerMeterSquared >= twoJoulesPerMeterSquared);
            Assert.False(twoJoulesPerMeterSquared < oneJoulePerMeterSquared);
            Assert.False(twoJoulesPerMeterSquared <= oneJoulePerMeterSquared);
        }

        [Fact]
        public void CompareToIsImplemented()
        {
            Irradiation joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            Assert.Equal(0, joulepermetersquared.CompareTo(joulepermetersquared));
            Assert.True(joulepermetersquared.CompareTo(Irradiation.Zero) > 0);
            Assert.True(Irradiation.Zero.CompareTo(joulepermetersquared) < 0);
        }

        [Fact]
        public void CompareToThrowsOnTypeMismatch()
        {
            Irradiation joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            Assert.Throws<ArgumentException>(() => joulepermetersquared.CompareTo(new object()));
        }

        [Fact]
        public void CompareToThrowsOnNull()
        {
            Irradiation joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            Assert.Throws<ArgumentNullException>(() => joulepermetersquared.CompareTo(null));
        }


        [Fact]
        public void EqualityOperators()
        {
            Irradiation a = Irradiation.FromJoulesPerMeterSquared(1);
            Irradiation b = Irradiation.FromJoulesPerMeterSquared(2);

// ReSharper disable EqualExpressionComparison
            Assert.True(a == a);
            Assert.True(a != b);

            Assert.False(a == b);
            Assert.False(a != a);
// ReSharper restore EqualExpressionComparison
        }

        [Fact]
        public void EqualsIsImplemented()
        {
            Irradiation v = Irradiation.FromJoulesPerMeterSquared(1);
            Assert.True(v.Equals(Irradiation.FromJoulesPerMeterSquared(1), Irradiation.FromJoulesPerMeterSquared(JoulesPerMeterSquaredTolerance)));
            Assert.False(v.Equals(Irradiation.Zero, Irradiation.FromJoulesPerMeterSquared(JoulesPerMeterSquaredTolerance)));
        }

        [Fact]
        public void EqualsReturnsFalseOnTypeMismatch()
        {
            Irradiation joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            Assert.False(joulepermetersquared.Equals(new object()));
        }

        [Fact]
        public void EqualsReturnsFalseOnNull()
        {
            Irradiation joulepermetersquared = Irradiation.FromJoulesPerMeterSquared(1);
            Assert.False(joulepermetersquared.Equals(null));
        }
    }
}
