using System;
using UnitsNet.Units;
using Xunit;

// Disable build warning CS1718: Comparison made to same variable; did you mean to compare something else?
#pragma warning disable 1718

// ReSharper disable once CheckNamespace
namespace UnitsNet.Tests
{
    /// <summary>
    /// Test of PowerGradient.
    /// </summary>
// ReSharper disable once PartialTypeWithSinglePart
    public abstract partial class PowerGradientTestsBase
    {
        protected abstract double KilowattsPerSecondInOneWattPerSecond { get; }
        protected abstract double WattsPerSecondInOneWattPerSecond { get; }

// ReSharper disable VirtualMemberNeverOverriden.Global
        protected virtual double KilowattsPerSecondTolerance { get { return 1e-5; } }
        protected virtual double WattsPerSecondTolerance { get { return 1e-5; } }
// ReSharper restore VirtualMemberNeverOverriden.Global

        [Fact]
        public void WattPerSecondToPowerGradientUnits()
        {
            PowerGradient wattpersecond = PowerGradient.FromWattsPerSecond(1);
            AssertEx.EqualTolerance(KilowattsPerSecondInOneWattPerSecond, wattpersecond.KilowattsPerSecond, KilowattsPerSecondTolerance);
            AssertEx.EqualTolerance(WattsPerSecondInOneWattPerSecond, wattpersecond.WattsPerSecond, WattsPerSecondTolerance);
        }

        [Fact]
        public void FromValueAndUnit()
        {
            AssertEx.EqualTolerance(1, PowerGradient.From(1, PowerGradientUnit.KilowattPerSecond).KilowattsPerSecond, KilowattsPerSecondTolerance);
            AssertEx.EqualTolerance(1, PowerGradient.From(1, PowerGradientUnit.WattPerSecond).WattsPerSecond, WattsPerSecondTolerance);
        }

        [Fact]
        public void As()
        {
            var wattpersecond = PowerGradient.FromWattsPerSecond(1);
            AssertEx.EqualTolerance(KilowattsPerSecondInOneWattPerSecond, wattpersecond.As(PowerGradientUnit.KilowattPerSecond), KilowattsPerSecondTolerance);
            AssertEx.EqualTolerance(WattsPerSecondInOneWattPerSecond, wattpersecond.As(PowerGradientUnit.WattPerSecond), WattsPerSecondTolerance);
        }

        [Fact]
        public void ConversionRoundTrip()
        {
            PowerGradient wattpersecond = PowerGradient.FromWattsPerSecond(1);
            AssertEx.EqualTolerance(1, PowerGradient.FromKilowattsPerSecond(wattpersecond.KilowattsPerSecond).WattsPerSecond, KilowattsPerSecondTolerance);
            AssertEx.EqualTolerance(1, PowerGradient.FromWattsPerSecond(wattpersecond.WattsPerSecond).WattsPerSecond, WattsPerSecondTolerance);
        }

        [Fact]
        public void ArithmeticOperators()
        {
            PowerGradient v = PowerGradient.FromWattsPerSecond(1);
            AssertEx.EqualTolerance(-1, -v.WattsPerSecond, WattsPerSecondTolerance);
            AssertEx.EqualTolerance(2, (PowerGradient.FromWattsPerSecond(3)-v).WattsPerSecond, WattsPerSecondTolerance);
            AssertEx.EqualTolerance(2, (v + v).WattsPerSecond, WattsPerSecondTolerance);
            AssertEx.EqualTolerance(10, (v*10).WattsPerSecond, WattsPerSecondTolerance);
            AssertEx.EqualTolerance(10, (10*v).WattsPerSecond, WattsPerSecondTolerance);
            AssertEx.EqualTolerance(2, (PowerGradient.FromWattsPerSecond(10)/5).WattsPerSecond, WattsPerSecondTolerance);
            AssertEx.EqualTolerance(2, PowerGradient.FromWattsPerSecond(10)/PowerGradient.FromWattsPerSecond(5), WattsPerSecondTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            PowerGradient oneWattPerSecond = PowerGradient.FromWattsPerSecond(1);
            PowerGradient twoWattsPerSecond = PowerGradient.FromWattsPerSecond(2);

            Assert.True(oneWattPerSecond < twoWattsPerSecond);
            Assert.True(oneWattPerSecond <= twoWattsPerSecond);
            Assert.True(twoWattsPerSecond > oneWattPerSecond);
            Assert.True(twoWattsPerSecond >= oneWattPerSecond);

            Assert.False(oneWattPerSecond > twoWattsPerSecond);
            Assert.False(oneWattPerSecond >= twoWattsPerSecond);
            Assert.False(twoWattsPerSecond < oneWattPerSecond);
            Assert.False(twoWattsPerSecond <= oneWattPerSecond);
        }

        [Fact]
        public void CompareToIsImplemented()
        {
            PowerGradient wattpersecond = PowerGradient.FromWattsPerSecond(1);
            Assert.Equal(0, wattpersecond.CompareTo(wattpersecond));
            Assert.True(wattpersecond.CompareTo(PowerGradient.Zero) > 0);
            Assert.True(PowerGradient.Zero.CompareTo(wattpersecond) < 0);
        }

        [Fact]
        public void CompareToThrowsOnTypeMismatch()
        {
            PowerGradient wattpersecond = PowerGradient.FromWattsPerSecond(1);
            Assert.Throws<ArgumentException>(() => wattpersecond.CompareTo(new object()));
        }

        [Fact]
        public void CompareToThrowsOnNull()
        {
            PowerGradient wattpersecond = PowerGradient.FromWattsPerSecond(1);
            Assert.Throws<ArgumentNullException>(() => wattpersecond.CompareTo(null));
        }


        [Fact]
        public void EqualityOperators()
        {
            PowerGradient a = PowerGradient.FromWattsPerSecond(1);
            PowerGradient b = PowerGradient.FromWattsPerSecond(2);

// ReSharper disable EqualExpressionComparison
            Assert.True(a == a);
            Assert.True(a != b);

            Assert.False(a == b);
            Assert.False(a != a);
// ReSharper restore EqualExpressionComparison
        }

        [Fact]
        public void EqualsIsImplemented()
        {
            PowerGradient v = PowerGradient.FromWattsPerSecond(1);
            Assert.True(v.Equals(PowerGradient.FromWattsPerSecond(1), PowerGradient.FromWattsPerSecond(WattsPerSecondTolerance)));
            Assert.False(v.Equals(PowerGradient.Zero, PowerGradient.FromWattsPerSecond(WattsPerSecondTolerance)));
        }

        [Fact]
        public void EqualsReturnsFalseOnTypeMismatch()
        {
            PowerGradient wattpersecond = PowerGradient.FromWattsPerSecond(1);
            Assert.False(wattpersecond.Equals(new object()));
        }

        [Fact]
        public void EqualsReturnsFalseOnNull()
        {
            PowerGradient wattpersecond = PowerGradient.FromWattsPerSecond(1);
            Assert.False(wattpersecond.Equals(null));
        }
    }
}
