using System;
using UnitsNet.Units;
using Xunit;

// Disable build warning CS1718: Comparison made to same variable; did you mean to compare something else?
#pragma warning disable 1718

// ReSharper disable once CheckNamespace
namespace UnitsNet.Tests
{
    /// <summary>
    /// Test of Precipitation.
    /// </summary>
// ReSharper disable once PartialTypeWithSinglePart
    public abstract partial class PrecipitationTestsBase
    {
        protected abstract double MillimetersPerMinuteInOneMillimeterPerMinute { get; }

// ReSharper disable VirtualMemberNeverOverriden.Global
        protected virtual double MillimetersPerMinuteTolerance { get { return 1e-5; } }
// ReSharper restore VirtualMemberNeverOverriden.Global

        [Fact]
        public void MillimeterPerMinuteToPrecipitationUnits()
        {
            Precipitation millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            AssertEx.EqualTolerance(MillimetersPerMinuteInOneMillimeterPerMinute, millimeterperminute.MillimetersPerMinute, MillimetersPerMinuteTolerance);
        }

        [Fact]
        public void FromValueAndUnit()
        {
            AssertEx.EqualTolerance(1, Precipitation.From(1, PrecipitationUnit.MillimeterPerMinute).MillimetersPerMinute, MillimetersPerMinuteTolerance);
        }

        [Fact]
        public void As()
        {
            var millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            AssertEx.EqualTolerance(MillimetersPerMinuteInOneMillimeterPerMinute, millimeterperminute.As(PrecipitationUnit.MillimeterPerMinute), MillimetersPerMinuteTolerance);
        }

        [Fact]
        public void ConversionRoundTrip()
        {
            Precipitation millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            AssertEx.EqualTolerance(1, Precipitation.FromMillimetersPerMinute(millimeterperminute.MillimetersPerMinute).MillimetersPerMinute, MillimetersPerMinuteTolerance);
        }

        [Fact]
        public void ArithmeticOperators()
        {
            Precipitation v = Precipitation.FromMillimetersPerMinute(1);
            AssertEx.EqualTolerance(-1, -v.MillimetersPerMinute, MillimetersPerMinuteTolerance);
            AssertEx.EqualTolerance(2, (Precipitation.FromMillimetersPerMinute(3)-v).MillimetersPerMinute, MillimetersPerMinuteTolerance);
            AssertEx.EqualTolerance(2, (v + v).MillimetersPerMinute, MillimetersPerMinuteTolerance);
            AssertEx.EqualTolerance(10, (v*10).MillimetersPerMinute, MillimetersPerMinuteTolerance);
            AssertEx.EqualTolerance(10, (10*v).MillimetersPerMinute, MillimetersPerMinuteTolerance);
            AssertEx.EqualTolerance(2, (Precipitation.FromMillimetersPerMinute(10)/5).MillimetersPerMinute, MillimetersPerMinuteTolerance);
            AssertEx.EqualTolerance(2, Precipitation.FromMillimetersPerMinute(10)/Precipitation.FromMillimetersPerMinute(5), MillimetersPerMinuteTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            Precipitation oneMillimeterPerMinute = Precipitation.FromMillimetersPerMinute(1);
            Precipitation twoMillimetersPerMinute = Precipitation.FromMillimetersPerMinute(2);

            Assert.True(oneMillimeterPerMinute < twoMillimetersPerMinute);
            Assert.True(oneMillimeterPerMinute <= twoMillimetersPerMinute);
            Assert.True(twoMillimetersPerMinute > oneMillimeterPerMinute);
            Assert.True(twoMillimetersPerMinute >= oneMillimeterPerMinute);

            Assert.False(oneMillimeterPerMinute > twoMillimetersPerMinute);
            Assert.False(oneMillimeterPerMinute >= twoMillimetersPerMinute);
            Assert.False(twoMillimetersPerMinute < oneMillimeterPerMinute);
            Assert.False(twoMillimetersPerMinute <= oneMillimeterPerMinute);
        }

        [Fact]
        public void CompareToIsImplemented()
        {
            Precipitation millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            Assert.Equal(0, millimeterperminute.CompareTo(millimeterperminute));
            Assert.True(millimeterperminute.CompareTo(Precipitation.Zero) > 0);
            Assert.True(Precipitation.Zero.CompareTo(millimeterperminute) < 0);
        }

        [Fact]
        public void CompareToThrowsOnTypeMismatch()
        {
            Precipitation millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            Assert.Throws<ArgumentException>(() => millimeterperminute.CompareTo(new object()));
        }

        [Fact]
        public void CompareToThrowsOnNull()
        {
            Precipitation millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            Assert.Throws<ArgumentNullException>(() => millimeterperminute.CompareTo(null));
        }


        [Fact]
        public void EqualityOperators()
        {
            Precipitation a = Precipitation.FromMillimetersPerMinute(1);
            Precipitation b = Precipitation.FromMillimetersPerMinute(2);

// ReSharper disable EqualExpressionComparison
            Assert.True(a == a);
            Assert.True(a != b);

            Assert.False(a == b);
            Assert.False(a != a);
// ReSharper restore EqualExpressionComparison
        }

        [Fact]
        public void EqualsIsImplemented()
        {
            Precipitation v = Precipitation.FromMillimetersPerMinute(1);
            Assert.True(v.Equals(Precipitation.FromMillimetersPerMinute(1), Precipitation.FromMillimetersPerMinute(MillimetersPerMinuteTolerance)));
            Assert.False(v.Equals(Precipitation.Zero, Precipitation.FromMillimetersPerMinute(MillimetersPerMinuteTolerance)));
        }

        [Fact]
        public void EqualsReturnsFalseOnTypeMismatch()
        {
            Precipitation millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            Assert.False(millimeterperminute.Equals(new object()));
        }

        [Fact]
        public void EqualsReturnsFalseOnNull()
        {
            Precipitation millimeterperminute = Precipitation.FromMillimetersPerMinute(1);
            Assert.False(millimeterperminute.Equals(null));
        }
    }
}
