            Assert.Equal(0, steradian.CompareTo(steradian));
            Assert.True(steradian.CompareTo(SolidAngle<double>.Zero) > 0);
            Assert.True(SolidAngle<double>.Zero.CompareTo(steradian) < 0);
        }

