        [Fact]
        public void ArithmeticOperators()
        {
            CellDensity v = CellDensity.FromCellsPerSquareCentimeter(1);
            AssertEx.EqualTolerance(-1, -v.CellsPerSquareCentimeter, CellsPerSquareCentimeterTolerance);
            AssertEx.EqualTolerance(2, (CellDensity.FromCellsPerSquareCentimeter(3)-v).CellsPerSquareCentimeter, CellsPerSquareCentimeterTolerance);
            AssertEx.EqualTolerance(2, (v + v).CellsPerSquareCentimeter, CellsPerSquareCentimeterTolerance);
            AssertEx.EqualTolerance(10, (v*10).CellsPerSquareCentimeter, CellsPerSquareCentimeterTolerance);
            AssertEx.EqualTolerance(10, (10*v).CellsPerSquareCentimeter, CellsPerSquareCentimeterTolerance);
            AssertEx.EqualTolerance(2, (CellDensity.FromCellsPerSquareCentimeter(10)/5).CellsPerSquareCentimeter, CellsPerSquareCentimeterTolerance);
            AssertEx.EqualTolerance(2, CellDensity.FromCellsPerSquareCentimeter(10)/CellDensity.FromCellsPerSquareCentimeter(5), CellsPerSquareCentimeterTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            CellDensity oneCellPerSquareCentimeter = CellDensity.FromCellsPerSquareCentimeter(1);
            CellDensity twoCellsPerSquareCentimeter = CellDensity.FromCellsPerSquareCentimeter(2);

            Assert.True(oneCellPerSquareCentimeter < twoCellsPerSquareCentimeter);
            Assert.True(oneCellPerSquareCentimeter <= twoCellsPerSquareCentimeter);
            Assert.True(twoCellsPerSquareCentimeter > oneCellPerSquareCentimeter);
            Assert.True(twoCellsPerSquareCentimeter >= oneCellPerSquareCentimeter);

            Assert.False(oneCellPerSquareCentimeter > twoCellsPerSquareCentimeter);
            Assert.False(oneCellPerSquareCentimeter >= twoCellsPerSquareCentimeter);
            Assert.False(twoCellsPerSquareCentimeter < oneCellPerSquareCentimeter);
            Assert.False(twoCellsPerSquareCentimeter <= oneCellPerSquareCentimeter);
        }

