        [Theory]
        [InlineData(1, CellDensityUnit.CellPerSquareCentimeter, 1, CellDensityUnit.CellPerSquareCentimeter, true)]  // Same value and unit.
        [InlineData(1, CellDensityUnit.CellPerSquareCentimeter, 2, CellDensityUnit.CellPerSquareCentimeter, false)] // Different value.
        [InlineData(2, CellDensityUnit.CellPerSquareCentimeter, 1, CellDensityUnit.CellPerMilliliter, false)] // Different value and unit.
        [InlineData(1, CellDensityUnit.CellPerSquareCentimeter, 1, CellDensityUnit.CellPerMilliliter, false)] // Different unit.
        public void Equals_ReturnsTrue_IfValueAndUnitAreEqual(double valueA, CellDensityUnit unitA, double valueB, CellDensityUnit unitB, bool expectEqual)
        {
            var a = new CellDensity(valueA, unitA);
            var b = new CellDensity(valueB, unitB);

            // Operator overloads.
            Assert.Equal(expectEqual, a == b);
            Assert.Equal(expectEqual, b == a);
            Assert.Equal(!expectEqual, a != b);
            Assert.Equal(!expectEqual, b != a);

            // IEquatable<T>
            Assert.Equal(expectEqual, a.Equals(b));
            Assert.Equal(expectEqual, b.Equals(a));

            // IEquatable
            Assert.Equal(expectEqual, a.Equals((object)b));
            Assert.Equal(expectEqual, b.Equals((object)a));
        }

        [Fact]
        public void Equals_Null_ReturnsFalse()
        {
            var a = CellDensity.Zero;

            Assert.False(a.Equals((object)null));

            // "The result of the expression is always 'false'..."
            #pragma warning disable CS8073
            Assert.False(a == null);
            Assert.False(null == a);
            Assert.True(a != null);
            Assert.True(null != a);
            #pragma warning restore CS8073
        }

