            try
            {
                CultureInfo.CurrentCulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 cells/cm²", new CellDensity(0.123456, CellDensityUnit.CellPerSquareCentimeter).ToString("s1"));
                Assert.Equal("0.12 cells/cm²", new CellDensity(0.123456, CellDensityUnit.CellPerSquareCentimeter).ToString("s2"));
                Assert.Equal("0.123 cells/cm²", new CellDensity(0.123456, CellDensityUnit.CellPerSquareCentimeter).ToString("s3"));
                Assert.Equal("0.1235 cells/cm²", new CellDensity(0.123456, CellDensityUnit.CellPerSquareCentimeter).ToString("s4"));
            }
            finally
