        [InlineData(-1.0)]
        public void NegationOperator_ReturnsQuantity_WithNegatedValue(double value)
        {
            var quantity = CellDensity.FromCellsPerSquareCentimeter(value);
            Assert.Equal(CellDensity.FromCellsPerSquareCentimeter(-value), -quantity);
        }
    }
}
