        [Fact]
        public void ToUnit()
        {
            var fraction = CloudCover.FromFractions(1);

            var fractionQuantity = fraction.ToUnit(CloudCoverUnit.Fraction);
            AssertEx.EqualTolerance(FractionsInOneFraction, (double)fractionQuantity.Value, FractionsTolerance);
            Assert.Equal(CloudCoverUnit.Fraction, fractionQuantity.Unit);

            var oktaQuantity = fraction.ToUnit(CloudCoverUnit.Okta);
            AssertEx.EqualTolerance(OktasInOneFraction, (double)oktaQuantity.Value, OktasTolerance);
            Assert.Equal(CloudCoverUnit.Okta, oktaQuantity.Unit);

            var percentQuantity = fraction.ToUnit(CloudCoverUnit.Percent);
            AssertEx.EqualTolerance(PercentInOneFraction, (double)percentQuantity.Value, PercentTolerance);
            Assert.Equal(CloudCoverUnit.Percent, percentQuantity.Unit);
        }

