        [Fact]
        public void ArithmeticOperators()
        {
            CloudCover v = CloudCover.FromFractions(1);
            AssertEx.EqualTolerance(-1, -v.Fractions, FractionsTolerance);
            AssertEx.EqualTolerance(2, (CloudCover.FromFractions(3)-v).Fractions, FractionsTolerance);
            AssertEx.EqualTolerance(2, (v + v).Fractions, FractionsTolerance);
            AssertEx.EqualTolerance(10, (v*10).Fractions, FractionsTolerance);
            AssertEx.EqualTolerance(10, (10*v).Fractions, FractionsTolerance);
            AssertEx.EqualTolerance(2, (CloudCover.FromFractions(10)/5).Fractions, FractionsTolerance);
            AssertEx.EqualTolerance(2, CloudCover.FromFractions(10)/CloudCover.FromFractions(5), FractionsTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            CloudCover oneFraction = CloudCover.FromFractions(1);
            CloudCover twoFractions = CloudCover.FromFractions(2);

            Assert.True(oneFraction < twoFractions);
            Assert.True(oneFraction <= twoFractions);
            Assert.True(twoFractions > oneFraction);
            Assert.True(twoFractions >= oneFraction);

            Assert.False(oneFraction > twoFractions);
            Assert.False(oneFraction >= twoFractions);
            Assert.False(twoFractions < oneFraction);
            Assert.False(twoFractions <= oneFraction);
        }

