                Assert.Equal(GradeUnit.Degree, parsed.Unit);
            }

            {
                Assert.True(Grade.TryParse("1 °", CultureInfo.GetCultureInfo("ru-RU"), out var parsed));
                AssertEx.EqualTolerance(1, parsed.Degrees, DegreesTolerance);
                Assert.Equal(GradeUnit.Degree, parsed.Unit);
            }

            {
                Assert.True(Grade.TryParse("1 ", CultureInfo.GetCultureInfo("en-US"), out var parsed));
                AssertEx.EqualTolerance(1, parsed.Fractions, FractionsTolerance);
                Assert.Equal(GradeUnit.Fraction, parsed.Unit);
            }

            {
                Assert.True(Grade.TryParse("1 %", CultureInfo.GetCultureInfo("en-US"), out var parsed));
                AssertEx.EqualTolerance(1, parsed.Percent, PercentTolerance);
                Assert.Equal(GradeUnit.Percent, parsed.Unit);
            }

            {
                Assert.True(Grade.TryParse("1 ‰", CultureInfo.GetCultureInfo("en-US"), out var parsed));
                AssertEx.EqualTolerance(1, parsed.Permille, PermilleTolerance);
                Assert.Equal(GradeUnit.Permille, parsed.Unit);
            }

        }

