            {
                Assert.True(Grade.TryParseUnit("‰", CultureInfo.GetCultureInfo("en-US"), out var parsedUnit));
                Assert.Equal(GradeUnit.Permille, parsedUnit);
            }

        }

        [Theory]
        [MemberData(nameof(UnitTypes))]
        public void ToUnit(GradeUnit unit)
        {
            var inBaseUnits = Grade.From(1.0, Grade.BaseUnit);
            var converted = inBaseUnits.ToUnit(unit);

            var conversionFactor = GetConversionFactor(unit);
            AssertEx.EqualTolerance(conversionFactor.UnitsInBaseUnit, converted.Value, conversionFactor.Tolerence);
            Assert.Equal(unit, converted.Unit);
        }

