        [Fact]
        public void ConversionRoundTrip()
        {
            Grade percent = Grade.FromPercent(1);
            AssertEx.EqualTolerance(1, Grade.FromDegrees(percent.Degrees).Percent, DegreesTolerance);
            AssertEx.EqualTolerance(1, Grade.FromFractions(percent.Fractions).Percent, FractionsTolerance);
            AssertEx.EqualTolerance(1, Grade.FromPercent(percent.Percent).Percent, PercentTolerance);
            AssertEx.EqualTolerance(1, Grade.FromPermille(percent.Permille).Percent, PermilleTolerance);
        }

        [Fact]
        public void ArithmeticOperators()
        {
            Grade v = Grade.FromPercent(1);
            AssertEx.EqualTolerance(-1, -v.Percent, PercentTolerance);
            AssertEx.EqualTolerance(2, (Grade.FromPercent(3)-v).Percent, PercentTolerance);
            AssertEx.EqualTolerance(2, (v + v).Percent, PercentTolerance);
            AssertEx.EqualTolerance(10, (v*10).Percent, PercentTolerance);
            AssertEx.EqualTolerance(10, (10*v).Percent, PercentTolerance);
            AssertEx.EqualTolerance(2, (Grade.FromPercent(10)/5).Percent, PercentTolerance);
            AssertEx.EqualTolerance(2, Grade.FromPercent(10)/Grade.FromPercent(5), PercentTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            Grade onePercent = Grade.FromPercent(1);
            Grade twoPercent = Grade.FromPercent(2);

            Assert.True(onePercent < twoPercent);
            Assert.True(onePercent <= twoPercent);
            Assert.True(twoPercent > onePercent);
            Assert.True(twoPercent >= onePercent);

            Assert.False(onePercent > twoPercent);
            Assert.False(onePercent >= twoPercent);
            Assert.False(twoPercent < onePercent);
            Assert.False(twoPercent <= onePercent);
        }

        [Fact]
