        [Theory]
        [InlineData(1, GradeUnit.Percent, 1, GradeUnit.Percent, true)]  // Same value and unit.
        [InlineData(1, GradeUnit.Percent, 2, GradeUnit.Percent, false)] // Different value.
        [InlineData(2, GradeUnit.Percent, 1, GradeUnit.Degree, false)] // Different value and unit.
        [InlineData(1, GradeUnit.Percent, 1, GradeUnit.Degree, false)] // Different unit.
        public void Equals_ReturnsTrue_IfValueAndUnitAreEqual(double valueA, GradeUnit unitA, double valueB, GradeUnit unitB, bool expectEqual)
        {
            var a = new Grade(valueA, unitA);
            var b = new Grade(valueB, unitB);

            // Operator overloads.
            Assert.Equal(expectEqual, a == b);
            Assert.Equal(expectEqual, b == a);
            Assert.Equal(!expectEqual, a != b);
            Assert.Equal(!expectEqual, b != a);

            // IEquatable<T>
            Assert.Equal(expectEqual, a.Equals(b));
            Assert.Equal(expectEqual, b.Equals(a));

            // IEquatable
            Assert.Equal(expectEqual, a.Equals((object)b));
            Assert.Equal(expectEqual, b.Equals((object)a));
        }

        [Fact]
        public void Equals_Null_ReturnsFalse()
        {
            var a = Grade.Zero;

            Assert.False(a.Equals((object)null));

            // "The result of the expression is always 'false'..."
            #pragma warning disable CS8073
            Assert.False(a == null);
            Assert.False(null == a);
            Assert.True(a != null);
            Assert.True(null != a);
            #pragma warning restore CS8073
        }

