        [Fact]
        public void ToString_ReturnsValueAndUnitAbbreviationInCurrentCulture()
        {
            var prevCulture = Thread.CurrentThread.CurrentCulture;
            Thread.CurrentThread.CurrentCulture = CultureInfo.GetCultureInfo("en-US");
            try {
                Assert.Equal("1 °", new Grade(1, GradeUnit.Degree).ToString());
                Assert.Equal("1", new Grade(1, GradeUnit.Fraction).ToString());
                Assert.Equal("1 %", new Grade(1, GradeUnit.Percent).ToString());
                Assert.Equal("1 ‰", new Grade(1, GradeUnit.Permille).ToString());
            }
            finally
            {
                Thread.CurrentThread.CurrentCulture = prevCulture;
            }
        }

        [Fact]
        public void ToString_WithSwedishCulture_ReturnsUnitAbbreviationForEnglishCultureSinceThereAreNoMappings()
        {
            // Chose this culture, because we don't currently have any abbreviations mapped for that culture and we expect the en-US to be used as fallback.
            var swedishCulture = CultureInfo.GetCultureInfo("sv-SE");

            Assert.Equal("1 °", new Grade(1, GradeUnit.Degree).ToString(swedishCulture));
            Assert.Equal("1", new Grade(1, GradeUnit.Fraction).ToString(swedishCulture));
            Assert.Equal("1 %", new Grade(1, GradeUnit.Percent).ToString(swedishCulture));
            Assert.Equal("1 ‰", new Grade(1, GradeUnit.Permille).ToString(swedishCulture));
        }

        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentCulture;
            try
            {
                CultureInfo.CurrentCulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 %", new Grade(0.123456, GradeUnit.Percent).ToString("s1"));
                Assert.Equal("0.12 %", new Grade(0.123456, GradeUnit.Percent).ToString("s2"));
                Assert.Equal("0.123 %", new Grade(0.123456, GradeUnit.Percent).ToString("s3"));
                Assert.Equal("0.1235 %", new Grade(0.123456, GradeUnit.Percent).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentCulture = oldCulture;
            }
        }

        [Fact]
        public void ToString_SFormatAndCulture_FormatsNumberWithGivenDigitsAfterRadixForGivenCulture()
        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 %", new Grade(0.123456, GradeUnit.Percent).ToString("s1", culture));
            Assert.Equal("0.12 %", new Grade(0.123456, GradeUnit.Percent).ToString("s2", culture));
            Assert.Equal("0.123 %", new Grade(0.123456, GradeUnit.Percent).ToString("s3", culture));
            Assert.Equal("0.1235 %", new Grade(0.123456, GradeUnit.Percent).ToString("s4", culture));
        }

