            AssertEx.EqualTolerance(2, (LinearNumberDensity.FromNumberPerMeter(3)-v).NumberPerMeter, NumberPerMeterTolerance);
            AssertEx.EqualTolerance(2, (v + v).NumberPerMeter, NumberPerMeterTolerance);
            AssertEx.EqualTolerance(10, (v*10).NumberPerMeter, NumberPerMeterTolerance);
            AssertEx.EqualTolerance(10, (10*v).NumberPerMeter, NumberPerMeterTolerance);
            AssertEx.EqualTolerance(2, (LinearNumberDensity.FromNumberPerMeter(10)/5).NumberPerMeter, NumberPerMeterTolerance);
            AssertEx.EqualTolerance(2, LinearNumberDensity.FromNumberPerMeter(10)/LinearNumberDensity.FromNumberPerMeter(5), NumberPerMeterTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            LinearNumberDensity oneNumberPerMeter = LinearNumberDensity.FromNumberPerMeter(1);
            LinearNumberDensity twoNumberPerMeter = LinearNumberDensity.FromNumberPerMeter(2);

            Assert.True(oneNumberPerMeter < twoNumberPerMeter);
            Assert.True(oneNumberPerMeter <= twoNumberPerMeter);
            Assert.True(twoNumberPerMeter > oneNumberPerMeter);
            Assert.True(twoNumberPerMeter >= oneNumberPerMeter);

            Assert.False(oneNumberPerMeter > twoNumberPerMeter);
            Assert.False(oneNumberPerMeter >= twoNumberPerMeter);
            Assert.False(twoNumberPerMeter < oneNumberPerMeter);
            Assert.False(twoNumberPerMeter <= oneNumberPerMeter);
        }

