        [Fact]
        public void ToString_ReturnsValueAndUnitAbbreviationInCurrentCulture()
        {
            var prevCulture = Thread.CurrentThread.CurrentUICulture;
            Thread.CurrentThread.CurrentUICulture = CultureInfo.GetCultureInfo("en-US");
            try {
                Assert.Equal("1 cm⁻¹", new LinearNumberDensity(1, LinearNumberDensityUnit.NumberPerCentimeter).ToString());
                Assert.Equal("1 m⁻¹", new LinearNumberDensity(1, LinearNumberDensityUnit.NumberPerMeter).ToString());
                Assert.Equal("1 mm⁻¹", new LinearNumberDensity(1, LinearNumberDensityUnit.NumberPerMillimeter).ToString());
            }
            finally
            {
                Thread.CurrentThread.CurrentUICulture = prevCulture;
            }
        }

        [Fact]
        public void ToString_WithSwedishCulture_ReturnsUnitAbbreviationForEnglishCultureSinceThereAreNoMappings()
        {
            // Chose this culture, because we don't currently have any abbreviations mapped for that culture and we expect the en-US to be used as fallback.
            var swedishCulture = CultureInfo.GetCultureInfo("sv-SE");

            Assert.Equal("1 cm⁻¹", new LinearNumberDensity(1, LinearNumberDensityUnit.NumberPerCentimeter).ToString(swedishCulture));
            Assert.Equal("1 m⁻¹", new LinearNumberDensity(1, LinearNumberDensityUnit.NumberPerMeter).ToString(swedishCulture));
            Assert.Equal("1 mm⁻¹", new LinearNumberDensity(1, LinearNumberDensityUnit.NumberPerMillimeter).ToString(swedishCulture));
        }

        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s1"));
                Assert.Equal("0.12 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s2"));
                Assert.Equal("0.123 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s3"));
                Assert.Equal("0.1235 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

        [Fact]
        public void ToString_SFormatAndCulture_FormatsNumberWithGivenDigitsAfterRadixForGivenCulture()
        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s1", culture));
            Assert.Equal("0.12 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s2", culture));
            Assert.Equal("0.123 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s3", culture));
            Assert.Equal("0.1235 m⁻¹", new LinearNumberDensity(0.123456, LinearNumberDensityUnit.NumberPerMeter).ToString("s4", culture));
        }

