                AssertEx.EqualTolerance(1, parsed.InverseCubicCentimeters, InverseCubicCentimetersTolerance);
                Assert.Equal(NumberConcentrationUnit.InverseCubicCentimeter, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

