                AssertEx.EqualTolerance(1, parsed.InverseCubicMeters, InverseCubicMetersTolerance);
                Assert.Equal(NumberConcentrationUnit.InverseCubicMeter, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

            try
