                var parsed = NumberConcentration.Parse("1 1/µm³", CultureInfo.GetCultureInfo("en-US"));
                AssertEx.EqualTolerance(1, parsed.InverseCubicMicrometers, InverseCubicMicrometersTolerance);
                Assert.Equal(NumberConcentrationUnit.InverseCubicMicrometer, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

            try
            {
                var parsed = NumberConcentration.Parse("1 1/mm³", CultureInfo.GetCultureInfo("en-US"));
                AssertEx.EqualTolerance(1, parsed.InverseCubicMillimeters, InverseCubicMillimetersTolerance);
                Assert.Equal(NumberConcentrationUnit.InverseCubicMillimeter, parsed.Unit);
            } catch (AmbiguousUnitParseException) { /* Some units have the same abbreviations */ }

        }

