        [Fact]
        public void ArithmeticOperators()
        {
            NumberConcentration v = NumberConcentration.FromInverseCubicMeters(1);
            AssertEx.EqualTolerance(-1, -v.InverseCubicMeters, InverseCubicMetersTolerance);
            AssertEx.EqualTolerance(2, (NumberConcentration.FromInverseCubicMeters(3)-v).InverseCubicMeters, InverseCubicMetersTolerance);
            AssertEx.EqualTolerance(2, (v + v).InverseCubicMeters, InverseCubicMetersTolerance);
            AssertEx.EqualTolerance(10, (v*10).InverseCubicMeters, InverseCubicMetersTolerance);
            AssertEx.EqualTolerance(10, (10*v).InverseCubicMeters, InverseCubicMetersTolerance);
            AssertEx.EqualTolerance(2, (NumberConcentration.FromInverseCubicMeters(10)/5).InverseCubicMeters, InverseCubicMetersTolerance);
            AssertEx.EqualTolerance(2, NumberConcentration.FromInverseCubicMeters(10)/NumberConcentration.FromInverseCubicMeters(5), InverseCubicMetersTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            NumberConcentration oneInverseCubicMeter = NumberConcentration.FromInverseCubicMeters(1);
            NumberConcentration twoInverseCubicMeters = NumberConcentration.FromInverseCubicMeters(2);

            Assert.True(oneInverseCubicMeter < twoInverseCubicMeters);
            Assert.True(oneInverseCubicMeter <= twoInverseCubicMeters);
            Assert.True(twoInverseCubicMeters > oneInverseCubicMeter);
            Assert.True(twoInverseCubicMeters >= oneInverseCubicMeter);

            Assert.False(oneInverseCubicMeter > twoInverseCubicMeters);
            Assert.False(oneInverseCubicMeter >= twoInverseCubicMeters);
            Assert.False(twoInverseCubicMeters < oneInverseCubicMeter);
            Assert.False(twoInverseCubicMeters <= oneInverseCubicMeter);
        }

