        [Theory]
        [InlineData(1, NumberConcentrationUnit.InverseCubicMeter, 1, NumberConcentrationUnit.InverseCubicMeter, true)]  // Same value and unit.
        [InlineData(1, NumberConcentrationUnit.InverseCubicMeter, 2, NumberConcentrationUnit.InverseCubicMeter, false)] // Different value.
        [InlineData(2, NumberConcentrationUnit.InverseCubicMeter, 1, NumberConcentrationUnit.InverseCubicCentimeter, false)] // Different value and unit.
        [InlineData(1, NumberConcentrationUnit.InverseCubicMeter, 1, NumberConcentrationUnit.InverseCubicCentimeter, false)] // Different unit.
        public void Equals_ReturnsTrue_IfValueAndUnitAreEqual(double valueA, NumberConcentrationUnit unitA, double valueB, NumberConcentrationUnit unitB, bool expectEqual)
        {
            var a = new NumberConcentration(valueA, unitA);
            var b = new NumberConcentration(valueB, unitB);

            // Operator overloads.
            Assert.Equal(expectEqual, a == b);
            Assert.Equal(expectEqual, b == a);
            Assert.Equal(!expectEqual, a != b);
            Assert.Equal(!expectEqual, b != a);

            // IEquatable<T>
            Assert.Equal(expectEqual, a.Equals(b));
            Assert.Equal(expectEqual, b.Equals(a));

            // IEquatable
            Assert.Equal(expectEqual, a.Equals((object)b));
            Assert.Equal(expectEqual, b.Equals((object)a));
        }

        [Fact]
        public void Equals_Null_ReturnsFalse()
        {
            var a = NumberConcentration.Zero;

            Assert.False(a.Equals((object)null));

            // "The result of the expression is always 'false'..."
            #pragma warning disable CS8073
            Assert.False(a == null);
            Assert.False(null == a);
            Assert.True(a != null);
            Assert.True(null != a);
            #pragma warning restore CS8073
        }

        [Fact]
        public void Equals_RelativeTolerance_IsImplemented()
        {
            var v = NumberConcentration.FromInverseCubicMeters(1);
            Assert.True(v.Equals(NumberConcentration.FromInverseCubicMeters(1), InverseCubicMetersTolerance, ComparisonType.Relative));
            Assert.False(v.Equals(NumberConcentration.Zero, InverseCubicMetersTolerance, ComparisonType.Relative));
            Assert.True(NumberConcentration.FromInverseCubicMeters(100).Equals(NumberConcentration.FromInverseCubicMeters(120), (double)0.3m, ComparisonType.Relative));
            Assert.False(NumberConcentration.FromInverseCubicMeters(100).Equals(NumberConcentration.FromInverseCubicMeters(120), (double)0.1m, ComparisonType.Relative));
        }

