            Assert.Equal("0.12 1/m³", new NumberConcentration(0.123456, NumberConcentrationUnit.InverseCubicMeter).ToString("s2", culture));
            Assert.Equal("0.123 1/m³", new NumberConcentration(0.123456, NumberConcentrationUnit.InverseCubicMeter).ToString("s3", culture));
            Assert.Equal("0.1235 1/m³", new NumberConcentration(0.123456, NumberConcentrationUnit.InverseCubicMeter).ToString("s4", culture));
        }

