        [Fact]
        public void ToUnit()
        {
            var numberpercubicmeter = NumberDensity.FromNumberPerCubicMeter(1);

            var numberpercubiccentimeterQuantity = numberpercubicmeter.ToUnit(NumberDensityUnit.NumberPerCubicCentimeter);
            AssertEx.EqualTolerance(NumberPerCubicCentimeterInOneNumberPerCubicMeter, (double)numberpercubiccentimeterQuantity.Value, NumberPerCubicCentimeterTolerance);
            Assert.Equal(NumberDensityUnit.NumberPerCubicCentimeter, numberpercubiccentimeterQuantity.Unit);

            var numberpercubicmeterQuantity = numberpercubicmeter.ToUnit(NumberDensityUnit.NumberPerCubicMeter);
            AssertEx.EqualTolerance(NumberPerCubicMeterInOneNumberPerCubicMeter, (double)numberpercubicmeterQuantity.Value, NumberPerCubicMeterTolerance);
            Assert.Equal(NumberDensityUnit.NumberPerCubicMeter, numberpercubicmeterQuantity.Unit);

            var numberpercubicmillimeterQuantity = numberpercubicmeter.ToUnit(NumberDensityUnit.NumberPerCubicMillimeter);
            AssertEx.EqualTolerance(NumberPerCubicMillimeterInOneNumberPerCubicMeter, (double)numberpercubicmillimeterQuantity.Value, NumberPerCubicMillimeterTolerance);
            Assert.Equal(NumberDensityUnit.NumberPerCubicMillimeter, numberpercubicmillimeterQuantity.Unit);
        }

