        [Fact]
        public void ArithmeticOperators()
        {
            NumberDensity v = NumberDensity.FromNumberPerCubicMeter(1);
            AssertEx.EqualTolerance(-1, -v.NumberPerCubicMeter, NumberPerCubicMeterTolerance);
            AssertEx.EqualTolerance(2, (NumberDensity.FromNumberPerCubicMeter(3)-v).NumberPerCubicMeter, NumberPerCubicMeterTolerance);
            AssertEx.EqualTolerance(2, (v + v).NumberPerCubicMeter, NumberPerCubicMeterTolerance);
            AssertEx.EqualTolerance(10, (v*10).NumberPerCubicMeter, NumberPerCubicMeterTolerance);
            AssertEx.EqualTolerance(10, (10*v).NumberPerCubicMeter, NumberPerCubicMeterTolerance);
            AssertEx.EqualTolerance(2, (NumberDensity.FromNumberPerCubicMeter(10)/5).NumberPerCubicMeter, NumberPerCubicMeterTolerance);
            AssertEx.EqualTolerance(2, NumberDensity.FromNumberPerCubicMeter(10)/NumberDensity.FromNumberPerCubicMeter(5), NumberPerCubicMeterTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            NumberDensity oneNumberPerCubicMeter = NumberDensity.FromNumberPerCubicMeter(1);
            NumberDensity twoNumberPerCubicMeter = NumberDensity.FromNumberPerCubicMeter(2);

            Assert.True(oneNumberPerCubicMeter < twoNumberPerCubicMeter);
            Assert.True(oneNumberPerCubicMeter <= twoNumberPerCubicMeter);
            Assert.True(twoNumberPerCubicMeter > oneNumberPerCubicMeter);
            Assert.True(twoNumberPerCubicMeter >= oneNumberPerCubicMeter);

            Assert.False(oneNumberPerCubicMeter > twoNumberPerCubicMeter);
            Assert.False(oneNumberPerCubicMeter >= twoNumberPerCubicMeter);
            Assert.False(twoNumberPerCubicMeter < oneNumberPerCubicMeter);
            Assert.False(twoNumberPerCubicMeter <= oneNumberPerCubicMeter);
        }

