        [Fact]
        public void ToUnit()
        {
            var becquerel = Radioactivity.FromBecquerels(1);

            var becquerelQuantity = becquerel.ToUnit(RadioactivityUnit.Becquerel);
            AssertEx.EqualTolerance(BecquerelsInOneBecquerel, (double)becquerelQuantity.Value, BecquerelsTolerance);
            Assert.Equal(RadioactivityUnit.Becquerel, becquerelQuantity.Unit);

            var curieQuantity = becquerel.ToUnit(RadioactivityUnit.Curie);
            AssertEx.EqualTolerance(CuriesInOneBecquerel, (double)curieQuantity.Value, CuriesTolerance);
            Assert.Equal(RadioactivityUnit.Curie, curieQuantity.Unit);

            var gigabecquerelQuantity = becquerel.ToUnit(RadioactivityUnit.Gigabecquerel);
            AssertEx.EqualTolerance(GigabecquerelsInOneBecquerel, (double)gigabecquerelQuantity.Value, GigabecquerelsTolerance);
            Assert.Equal(RadioactivityUnit.Gigabecquerel, gigabecquerelQuantity.Unit);

            var kilobecquerelQuantity = becquerel.ToUnit(RadioactivityUnit.Kilobecquerel);
            AssertEx.EqualTolerance(KilobecquerelsInOneBecquerel, (double)kilobecquerelQuantity.Value, KilobecquerelsTolerance);
            Assert.Equal(RadioactivityUnit.Kilobecquerel, kilobecquerelQuantity.Unit);

            var megabecquerelQuantity = becquerel.ToUnit(RadioactivityUnit.Megabecquerel);
            AssertEx.EqualTolerance(MegabecquerelsInOneBecquerel, (double)megabecquerelQuantity.Value, MegabecquerelsTolerance);
            Assert.Equal(RadioactivityUnit.Megabecquerel, megabecquerelQuantity.Unit);

            var microcurieQuantity = becquerel.ToUnit(RadioactivityUnit.Microcurie);
            AssertEx.EqualTolerance(MicrocuriesInOneBecquerel, (double)microcurieQuantity.Value, MicrocuriesTolerance);
            Assert.Equal(RadioactivityUnit.Microcurie, microcurieQuantity.Unit);

            var millicurieQuantity = becquerel.ToUnit(RadioactivityUnit.Millicurie);
            AssertEx.EqualTolerance(MillicuriesInOneBecquerel, (double)millicurieQuantity.Value, MillicuriesTolerance);
            Assert.Equal(RadioactivityUnit.Millicurie, millicurieQuantity.Unit);

            var terabecquerelQuantity = becquerel.ToUnit(RadioactivityUnit.Terabecquerel);
            AssertEx.EqualTolerance(TerabecquerelsInOneBecquerel, (double)terabecquerelQuantity.Value, TerabecquerelsTolerance);
            Assert.Equal(RadioactivityUnit.Terabecquerel, terabecquerelQuantity.Unit);
        }

