        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        private T GetValueInBaseUnit()
        {
            switch(Unit)
            {
                case AngleUnit.Arcminute: return Value/60;
                case AngleUnit.Arcsecond: return Value/3600;
                case AngleUnit.Centiradian: return (Value*180/Math.PI) * 1e-2d;
                case AngleUnit.Deciradian: return (Value*180/Math.PI) * 1e-1d;
                case AngleUnit.Degree: return Value;
                case AngleUnit.Gradian: return Value*0.9;
                case AngleUnit.Microdegree: return (Value) * 1e-6d;
                case AngleUnit.Microradian: return (Value*180/Math.PI) * 1e-6d;
                case AngleUnit.Millidegree: return (Value) * 1e-3d;
                case AngleUnit.Milliradian: return (Value*180/Math.PI) * 1e-3d;
                case AngleUnit.Nanodegree: return (Value) * 1e-9d;
                case AngleUnit.Nanoradian: return (Value*180/Math.PI) * 1e-9d;
                case AngleUnit.Radian: return Value*180/Math.PI;
                case AngleUnit.Revolution: return Value*360;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to base units.");
            }
        }

        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        internal Angle<T> ToBaseUnit()
        {
            var baseUnitValue = GetValueInBaseUnit();
            return new Angle<T>(baseUnitValue, BaseUnit);
        }

        private T GetValueAs(AngleUnit unit)
        {
            if(Unit == unit)
                return Value;

            var baseUnitValue = GetValueInBaseUnit();

            switch(unit)
            {
                case AngleUnit.Arcminute: return baseUnitValue*60;
                case AngleUnit.Arcsecond: return baseUnitValue*3600;
                case AngleUnit.Centiradian: return (baseUnitValue/180*Math.PI) / 1e-2d;
                case AngleUnit.Deciradian: return (baseUnitValue/180*Math.PI) / 1e-1d;
                case AngleUnit.Degree: return baseUnitValue;
                case AngleUnit.Gradian: return baseUnitValue/0.9;
                case AngleUnit.Microdegree: return (baseUnitValue) / 1e-6d;
                case AngleUnit.Microradian: return (baseUnitValue/180*Math.PI) / 1e-6d;
                case AngleUnit.Millidegree: return (baseUnitValue) / 1e-3d;
                case AngleUnit.Milliradian: return (baseUnitValue/180*Math.PI) / 1e-3d;
                case AngleUnit.Nanodegree: return (baseUnitValue) / 1e-9d;
                case AngleUnit.Nanoradian: return (baseUnitValue/180*Math.PI) / 1e-9d;
                case AngleUnit.Radian: return baseUnitValue/180*Math.PI;
                case AngleUnit.Revolution: return baseUnitValue/360;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

