        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        private T GetValueInBaseUnit()
        {
            switch(Unit)
            {
                case AreaUnit.Acre: return Value*4046.85642;
                case AreaUnit.Hectare: return Value*1e4;
                case AreaUnit.SquareCentimeter: return Value*1e-4;
                case AreaUnit.SquareDecimeter: return Value*1e-2;
                case AreaUnit.SquareFoot: return Value*0.092903;
                case AreaUnit.SquareInch: return Value*0.00064516;
                case AreaUnit.SquareKilometer: return Value*1e6;
                case AreaUnit.SquareMeter: return Value;
                case AreaUnit.SquareMicrometer: return Value*1e-12;
                case AreaUnit.SquareMile: return Value*2.59e6;
                case AreaUnit.SquareMillimeter: return Value*1e-6;
                case AreaUnit.SquareNauticalMile: return Value*3429904;
                case AreaUnit.SquareYard: return Value*0.836127;
                case AreaUnit.UsSurveySquareFoot: return Value*0.09290341161;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to base units.");
            }
        }

        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        internal Area<T> ToBaseUnit()
        {
            var baseUnitValue = GetValueInBaseUnit();
            return new Area<T>(baseUnitValue, BaseUnit);
        }

        private T GetValueAs(AreaUnit unit)
        {
            if(Unit == unit)
                return Value;

            var baseUnitValue = GetValueInBaseUnit();

            switch(unit)
            {
                case AreaUnit.Acre: return baseUnitValue/4046.85642;
                case AreaUnit.Hectare: return baseUnitValue/1e4;
                case AreaUnit.SquareCentimeter: return baseUnitValue/1e-4;
                case AreaUnit.SquareDecimeter: return baseUnitValue/1e-2;
                case AreaUnit.SquareFoot: return baseUnitValue/0.092903;
                case AreaUnit.SquareInch: return baseUnitValue/0.00064516;
                case AreaUnit.SquareKilometer: return baseUnitValue/1e6;
                case AreaUnit.SquareMeter: return baseUnitValue;
                case AreaUnit.SquareMicrometer: return baseUnitValue/1e-12;
                case AreaUnit.SquareMile: return baseUnitValue/2.59e6;
                case AreaUnit.SquareMillimeter: return baseUnitValue/1e-6;
                case AreaUnit.SquareNauticalMile: return baseUnitValue/3429904;
                case AreaUnit.SquareYard: return baseUnitValue/0.836127;
                case AreaUnit.UsSurveySquareFoot: return baseUnitValue/0.09290341161;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

