        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        private T GetValueInBaseUnit()
        {
            switch(Unit)
            {
                case FrequencyUnit.BeatPerMinute: return Value/60;
                case FrequencyUnit.CyclePerHour: return Value/3600;
                case FrequencyUnit.CyclePerMinute: return Value/60;
                case FrequencyUnit.Gigahertz: return (Value) * 1e9d;
                case FrequencyUnit.Hertz: return Value;
                case FrequencyUnit.Kilohertz: return (Value) * 1e3d;
                case FrequencyUnit.Megahertz: return (Value) * 1e6d;
                case FrequencyUnit.PerSecond: return Value;
                case FrequencyUnit.RadianPerSecond: return Value/6.2831853072;
                case FrequencyUnit.Terahertz: return (Value) * 1e12d;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to base units.");
            }
        }

        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        internal Frequency<T> ToBaseUnit()
        {
            var baseUnitValue = GetValueInBaseUnit();
            return new Frequency<T>(baseUnitValue, BaseUnit);
        }

        private T GetValueAs(FrequencyUnit unit)
        {
            if(Unit == unit)
                return Value;

            var baseUnitValue = GetValueInBaseUnit();

            switch(unit)
            {
                case FrequencyUnit.BeatPerMinute: return baseUnitValue*60;
                case FrequencyUnit.CyclePerHour: return baseUnitValue*3600;
                case FrequencyUnit.CyclePerMinute: return baseUnitValue*60;
                case FrequencyUnit.Gigahertz: return (baseUnitValue) / 1e9d;
                case FrequencyUnit.Hertz: return baseUnitValue;
                case FrequencyUnit.Kilohertz: return (baseUnitValue) / 1e3d;
                case FrequencyUnit.Megahertz: return (baseUnitValue) / 1e6d;
                case FrequencyUnit.PerSecond: return baseUnitValue;
                case FrequencyUnit.RadianPerSecond: return baseUnitValue*6.2831853072;
                case FrequencyUnit.Terahertz: return (baseUnitValue) / 1e12d;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

