        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        private T GetValueInBaseUnit()
        {
            switch(Unit)
            {
                case InformationUnit.Bit: return Value;
                case InformationUnit.Byte: return Value*8m;
                case InformationUnit.Exabit: return (Value) * 1e18m;
                case InformationUnit.Exabyte: return (Value*8m) * 1e18m;
                case InformationUnit.Exbibit: return (Value) * (1024m * 1024 * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Exbibyte: return (Value*8m) * (1024m * 1024 * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Gibibit: return (Value) * (1024m * 1024 * 1024);
                case InformationUnit.Gibibyte: return (Value*8m) * (1024m * 1024 * 1024);
                case InformationUnit.Gigabit: return (Value) * 1e9m;
                case InformationUnit.Gigabyte: return (Value*8m) * 1e9m;
                case InformationUnit.Kibibit: return (Value) * 1024m;
                case InformationUnit.Kibibyte: return (Value*8m) * 1024m;
                case InformationUnit.Kilobit: return (Value) * 1e3m;
                case InformationUnit.Kilobyte: return (Value*8m) * 1e3m;
                case InformationUnit.Mebibit: return (Value) * (1024m * 1024);
                case InformationUnit.Mebibyte: return (Value*8m) * (1024m * 1024);
                case InformationUnit.Megabit: return (Value) * 1e6m;
                case InformationUnit.Megabyte: return (Value*8m) * 1e6m;
                case InformationUnit.Pebibit: return (Value) * (1024m * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Pebibyte: return (Value*8m) * (1024m * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Petabit: return (Value) * 1e15m;
                case InformationUnit.Petabyte: return (Value*8m) * 1e15m;
                case InformationUnit.Tebibit: return (Value) * (1024m * 1024 * 1024 * 1024);
                case InformationUnit.Tebibyte: return (Value*8m) * (1024m * 1024 * 1024 * 1024);
                case InformationUnit.Terabit: return (Value) * 1e12m;
                case InformationUnit.Terabyte: return (Value*8m) * 1e12m;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to base units.");
            }
        }

        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        internal Information<T> ToBaseUnit()
        {
            var baseUnitValue = GetValueInBaseUnit();
            return new Information<T>(baseUnitValue, BaseUnit);
        }

        private T GetValueAs(InformationUnit unit)
        {
            if(Unit == unit)
                return Value;

            var baseUnitValue = GetValueInBaseUnit();

            switch(unit)
            {
                case InformationUnit.Bit: return baseUnitValue;
                case InformationUnit.Byte: return baseUnitValue/8m;
                case InformationUnit.Exabit: return (baseUnitValue) / 1e18m;
                case InformationUnit.Exabyte: return (baseUnitValue/8m) / 1e18m;
                case InformationUnit.Exbibit: return (baseUnitValue) / (1024m * 1024 * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Exbibyte: return (baseUnitValue/8m) / (1024m * 1024 * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Gibibit: return (baseUnitValue) / (1024m * 1024 * 1024);
                case InformationUnit.Gibibyte: return (baseUnitValue/8m) / (1024m * 1024 * 1024);
                case InformationUnit.Gigabit: return (baseUnitValue) / 1e9m;
                case InformationUnit.Gigabyte: return (baseUnitValue/8m) / 1e9m;
                case InformationUnit.Kibibit: return (baseUnitValue) / 1024m;
                case InformationUnit.Kibibyte: return (baseUnitValue/8m) / 1024m;
                case InformationUnit.Kilobit: return (baseUnitValue) / 1e3m;
                case InformationUnit.Kilobyte: return (baseUnitValue/8m) / 1e3m;
                case InformationUnit.Mebibit: return (baseUnitValue) / (1024m * 1024);
                case InformationUnit.Mebibyte: return (baseUnitValue/8m) / (1024m * 1024);
                case InformationUnit.Megabit: return (baseUnitValue) / 1e6m;
                case InformationUnit.Megabyte: return (baseUnitValue/8m) / 1e6m;
                case InformationUnit.Pebibit: return (baseUnitValue) / (1024m * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Pebibyte: return (baseUnitValue/8m) / (1024m * 1024 * 1024 * 1024 * 1024);
                case InformationUnit.Petabit: return (baseUnitValue) / 1e15m;
                case InformationUnit.Petabyte: return (baseUnitValue/8m) / 1e15m;
                case InformationUnit.Tebibit: return (baseUnitValue) / (1024m * 1024 * 1024 * 1024);
                case InformationUnit.Tebibyte: return (baseUnitValue/8m) / (1024m * 1024 * 1024 * 1024);
                case InformationUnit.Terabit: return (baseUnitValue) / 1e12m;
                case InformationUnit.Terabyte: return (baseUnitValue/8m) / 1e12m;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

