        {
            if(!(unit is LengthUnit unitAsLengthUnit))
                throw new ArgumentException($"The given unit is of type {unit.GetType()}. Only {typeof(LengthUnit)} is supported.", nameof(unit));

            return As(unitAsLengthUnit);
        }

