        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        private T GetValueInBaseUnit()
        {
            switch(Unit)
            {
                case MolarityUnit.CentimolesPerLiter: return (Value/1e-3) * 1e-2d;
                case MolarityUnit.DecimolesPerLiter: return (Value/1e-3) * 1e-1d;
                case MolarityUnit.MicromolesPerLiter: return (Value/1e-3) * 1e-6d;
                case MolarityUnit.MillimolesPerLiter: return (Value/1e-3) * 1e-3d;
                case MolarityUnit.MolesPerCubicMeter: return Value;
                case MolarityUnit.MolesPerLiter: return Value/1e-3;
                case MolarityUnit.NanomolesPerLiter: return (Value/1e-3) * 1e-9d;
                case MolarityUnit.PicomolesPerLiter: return (Value/1e-3) * 1e-12d;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to base units.");
            }
        }

        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        internal Molarity<T> ToBaseUnit()
        {
            var baseUnitValue = GetValueInBaseUnit();
            return new Molarity<T>(baseUnitValue, BaseUnit);
        }

        private T GetValueAs(MolarityUnit unit)
        {
            if(Unit == unit)
                return Value;

            var baseUnitValue = GetValueInBaseUnit();

            switch(unit)
            {
                case MolarityUnit.CentimolesPerLiter: return (baseUnitValue*1e-3) / 1e-2d;
                case MolarityUnit.DecimolesPerLiter: return (baseUnitValue*1e-3) / 1e-1d;
                case MolarityUnit.MicromolesPerLiter: return (baseUnitValue*1e-3) / 1e-6d;
                case MolarityUnit.MillimolesPerLiter: return (baseUnitValue*1e-3) / 1e-3d;
                case MolarityUnit.MolesPerCubicMeter: return baseUnitValue;
                case MolarityUnit.MolesPerLiter: return baseUnitValue*1e-3;
                case MolarityUnit.NanomolesPerLiter: return (baseUnitValue*1e-3) / 1e-9d;
                case MolarityUnit.PicomolesPerLiter: return (baseUnitValue*1e-3) / 1e-12d;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

