                throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

        /// <summary>
        ///     Attempts to convert this <see cref="NumberConcentration"/> to another <see cref="NumberConcentration"/> with the unit representation <paramref name="unit" />.
        /// </summary>
        /// <param name="unit">The unit to convert to.</param>
        /// <param name="converted">The converted <see cref="NumberConcentration"/> in <paramref name="unit"/>, if successful.</param>
        /// <returns>True if successful, otherwise false.</returns>
        private bool TryToUnit(NumberConcentrationUnit unit, [NotNullWhen(true)] out NumberConcentration? converted)
        {
            if (Unit == unit)
            {
                converted = this;
                return true;
            }

            NumberConcentration? convertedOrNull = (Unit, unit) switch
            {
                // NumberConcentrationUnit -> BaseUnit
                (NumberConcentrationUnit.InverseCubicCentimeter, NumberConcentrationUnit.InverseCubicMeter) => new NumberConcentration(_value / 1e-4, NumberConcentrationUnit.InverseCubicMeter),
                (NumberConcentrationUnit.InverseCubicMicrometer, NumberConcentrationUnit.InverseCubicMeter) => new NumberConcentration(_value / 1e-12, NumberConcentrationUnit.InverseCubicMeter),
                (NumberConcentrationUnit.InverseCubicMillimeter, NumberConcentrationUnit.InverseCubicMeter) => new NumberConcentration(_value / 1e-6, NumberConcentrationUnit.InverseCubicMeter),

                // BaseUnit -> NumberConcentrationUnit
                (NumberConcentrationUnit.InverseCubicMeter, NumberConcentrationUnit.InverseCubicCentimeter) => new NumberConcentration(_value * 1e-4, NumberConcentrationUnit.InverseCubicCentimeter),
                (NumberConcentrationUnit.InverseCubicMeter, NumberConcentrationUnit.InverseCubicMicrometer) => new NumberConcentration(_value * 1e-12, NumberConcentrationUnit.InverseCubicMicrometer),
                (NumberConcentrationUnit.InverseCubicMeter, NumberConcentrationUnit.InverseCubicMillimeter) => new NumberConcentration(_value * 1e-6, NumberConcentrationUnit.InverseCubicMillimeter),

                _ => null
            };

            if (convertedOrNull is null)
            {
                converted = default;
                return false;
            }

            converted = convertedOrNull.Value;
            return true;
        }

