        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        private double GetValueInBaseUnit()
        {
            switch(Unit)
            {
                case RadioactivityUnit.Becquerel: return _value;
                case RadioactivityUnit.Curie: return _value*3.7e10;
                case RadioactivityUnit.Gigabecquerel: return (_value) * 1e9d;
                case RadioactivityUnit.Kilobecquerel: return (_value) * 1e3d;
                case RadioactivityUnit.Megabecquerel: return (_value) * 1e6d;
                case RadioactivityUnit.Microcurie: return (_value*3.7e10) * 1e-6d;
                case RadioactivityUnit.Millicurie: return (_value*3.7e10) * 1e-3d;
                case RadioactivityUnit.Terabecquerel: return (_value) * 1e12d;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to base units.");
            }
        }

        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        internal Radioactivity ToBaseUnit()
        {
            var baseUnitValue = GetValueInBaseUnit();
            return new Radioactivity(baseUnitValue, BaseUnit);
        }

        private double GetValueAs(RadioactivityUnit unit)
        {
            if(Unit == unit)
                return _value;

            var baseUnitValue = GetValueInBaseUnit();

            switch(unit)
            {
                case RadioactivityUnit.Becquerel: return baseUnitValue;
                case RadioactivityUnit.Curie: return baseUnitValue/3.7e10;
                case RadioactivityUnit.Gigabecquerel: return (baseUnitValue) / 1e9d;
                case RadioactivityUnit.Kilobecquerel: return (baseUnitValue) / 1e3d;
                case RadioactivityUnit.Megabecquerel: return (baseUnitValue) / 1e6d;
                case RadioactivityUnit.Microcurie: return (baseUnitValue/3.7e10) / 1e-6d;
                case RadioactivityUnit.Millicurie: return (baseUnitValue/3.7e10) / 1e-3d;
                case RadioactivityUnit.Terabecquerel: return (baseUnitValue) / 1e12d;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

