        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        private T GetValueInBaseUnit()
        {
            switch(Unit)
            {
                case TemperatureUnit.DegreeCelsius: return Value + 273.15;
                case TemperatureUnit.DegreeDelisle: return Value*-2/3 + 373.15;
                case TemperatureUnit.DegreeFahrenheit: return Value*5/9 + 459.67*5/9;
                case TemperatureUnit.DegreeNewton: return Value*100/33 + 273.15;
                case TemperatureUnit.DegreeRankine: return Value*5/9;
                case TemperatureUnit.DegreeReaumur: return Value*5/4 + 273.15;
                case TemperatureUnit.DegreeRoemer: return Value*40/21 + 273.15 - 7.5*40d/21;
                case TemperatureUnit.Kelvin: return Value;
                case TemperatureUnit.MillidegreeCelsius: return Value / 1000 + 273.15;
                case TemperatureUnit.SolarTemperature: return Value * 5778;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to base units.");
            }
        }

        /// <summary>
        ///     Converts the current value + unit to the base unit.
        ///     This is typically the first step in converting from one unit to another.
        /// </summary>
        /// <returns>The value in the base unit representation.</returns>
        internal Temperature<T> ToBaseUnit()
        {
            var baseUnitValue = GetValueInBaseUnit();
            return new Temperature<T>(baseUnitValue, BaseUnit);
        }

        private T GetValueAs(TemperatureUnit unit)
        {
            if(Unit == unit)
                return Value;

            var baseUnitValue = GetValueInBaseUnit();

            switch(unit)
            {
                case TemperatureUnit.DegreeCelsius: return baseUnitValue - 273.15;
                case TemperatureUnit.DegreeDelisle: return (baseUnitValue - 373.15)*-3/2;
                case TemperatureUnit.DegreeFahrenheit: return (baseUnitValue - 459.67*5/9)*9/5;
                case TemperatureUnit.DegreeNewton: return (baseUnitValue - 273.15)*33/100;
                case TemperatureUnit.DegreeRankine: return baseUnitValue*9/5;
                case TemperatureUnit.DegreeReaumur: return (baseUnitValue - 273.15)*4/5;
                case TemperatureUnit.DegreeRoemer: return (baseUnitValue - (273.15 - 7.5*40d/21))*21/40;
                case TemperatureUnit.Kelvin: return baseUnitValue;
                case TemperatureUnit.MillidegreeCelsius: return (baseUnitValue - 273.15) * 1000;
                case TemperatureUnit.SolarTemperature: return baseUnitValue / 5778;
                default:
                    throw new NotImplementedException($"Can not convert {Unit} to {unit}.");
            }
        }

